(function(){"use strict";const selectors={triggerArray:".js-trigger-modal",closeButtons:".js-modal-close",modalContent:".modal__content",modal:".modal"};const classes={modalOpen:"modal--open",modalActive:"modal--active"};const openModal=event=>{const targetModal=event.currentTarget.nextElementSibling;targetModal.classList.add(classes.modalOpen);document.body.classList.add(classes.modalActive)};const closeModal=event=>{const targetModal=event.currentTarget.closest(selectors.modal);targetModal.classList.remove(classes.modalOpen);document.body.classList.remove(classes.modalActive)};const closeModalOutside=event=>{event.currentTarget.classList.remove(classes.modalOpen);document.body.classList.remove(classes.modalActive)};document.querySelectorAll(selectors.triggerArray).forEach((e=>{e.addEventListener("click",openModal)}));document.querySelectorAll(selectors.modal).forEach((e=>{e.addEventListener("click",(function(event){event.currentTarget===event.target?closeModalOutside(event):null}))}));document.querySelectorAll(selectors.closeButtons).forEach((e=>{e.addEventListener("click",(function(event){closeModal(event)}))}))})();